 select a.transacId,a.transacType,a.billNumber,a.buyerId,a.sellerId,a.amount,a.transacStatus,a.transacDate,a.intentionStatus,unix_timestamp(a.updateTimeStamp) as updateTimeStamp,b.quoteId,b.quoteAmount,b.quoterId,b.interest,b.xPerLakh,b.real_money,b.quoteDate,c.billType,c.amount,c.billId,c.acceptor,c.maturity,TIMESTAMPDIFF(day,#{jsonObject.curr_time},c.maturity)as remain_days,c.status,c.releaseDate,c.releaserId,c.billPicsId,c.transferable ,c.billReferer,d.* from (select * from pengpengpiao.ppp_transaction where buyerId = #{jsonObject.uuid}  

	and intentionStatus  in
	#{item}

 ) a left join (select * from pengpengpiao.ppp_quote where 1=1   

	and status  in
	#{item}

 ) b on a.billNumber = b.billNumber left join (select * from pengpengpiao.ppp_bill where 1=1  

	and status in
	#{item}

  ) c on a.billNumber = c.billNumber  LEFT JOIN(select * from pengpengpiao.ppp_company ) d ON a.sellerId =  d.contactsId ORDER BY a.updateTimeStamp DESC limit #{jsonObject.currentPage},#{jsonObject.pageSize};

