select a.billId,a.billNumber,a.billType,a.acceptor,a.billReferer,a.amount,a.maturity,TIMESTAMPDIFF(day,#{jsonObject.curr_time},a.maturity)as remain_days,a.status,a.releaseDate,a.releaserId,a.billPicsId,a.transferable,a.billReferer,case when b.countNumber is null then 0 else b.countNumber end as count,case when b.status is null then "未报价" else b.status end as quoteStatus from 
	(select * from pengpengpiao.ppp_bill  

		1=1 


				and billType = #{jsonObject.billType} 


				and amount >= ${jsonObject.minAmount}


				 and amount < ${jsonObject.maxAmount}


				 and maturity >= #{jsonObject.minDate}


				 and maturity < #{jsonObject.maxDate}


		and 1=1 and status="审核完成" and billReferer='传统渠道')  a  left join  (select billNumber,status, count(*) as countNumber  from pengpengpiao.ppp_quote group by billNumber,status)  b on a.billNumber=b.billNumber  
		where b.status = "报价中" or b.status is null order by a.releaseDate desc limit ${jsonObject.starter},${jsonObject.number }

select count(*) from 
	(select * from pengpengpiao.ppp_bill  

		1=1 


				and billType = #{jsonObject.billType} 


				and amount >= ${jsonObject.minAmount}


				 and amount < ${jsonObject.maxAmount}


				 and maturity >= #{jsonObject.minDate}


